import React, { useEffect, useState } from "react";
import axios from "axios";
import "./Contas.css";

function Contas() {
  const [form, setForm] = useState({
    nome: "",
    corretora: "Binance",
    api_key: "",
    secret_key: "",
    status: "offline"
  });
  const [contas, setContas] = useState([]);
  const [editId, setEditId] = useState(null);
const carregarContas = async () => {
  try {
    const res = await axios.get("http://localhost:5000/api/contas");
    const contasRecebidas = res.data;

    // Testar status de cada conta individualmente
    const atualizadas = await Promise.all(
      contasRecebidas.map(async (conta) => {
        try {
          const resp = await axios.post("http://localhost:5000/api/contas/testar", {
            api_key: conta.api_key,
            secret_key: conta.secret_key,
            corretora: conta.corretora,
          });

          const statusAtual = resp.data.status === "online" ? "online" : "offline";

          // Atualiza no banco se status mudou
          if (conta.status !== statusAtual) {
            await axios.put(`http://localhost:5000/api/contas/${conta.id}`, {
              ...conta,
              status: statusAtual,
            });
          }

          return { ...conta, status: statusAtual };
        } catch {
          return { ...conta, status: "offline" }; // Falha = offline
        }
      })
    );

    setContas(atualizadas);
  } catch (err) {
    console.error("Erro ao carregar contas:", err);
  }
};


  useEffect(() => {
    carregarContas();
  }, []);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editId) {
        await axios.put(`http://localhost:5000/api/contas/${editId}`, form);
      } else {
        await axios.post("http://localhost:5000/api/contas", form);
      }
      setForm({
        nome: "",
        corretora: "Binance",
        api_key: "",
        secret_key: "",
        status: "offline"
      });
      setEditId(null);
      carregarContas();
    } catch (err) {
      alert("Erro ao salvar conta");
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm("Deseja realmente excluir esta conta?")) {
      await axios.delete(`http://localhost:5000/api/contas/${id}`);
      carregarContas();
    }
  };

  const handleEdit = (conta) => {
    setForm({
      nome: conta.nome,
      corretora: conta.corretora,
      api_key: conta.api_key,
      secret_key: conta.secret_key,
      status: conta.status
    });
    setEditId(conta.id);
  };

  const testarConexao = async () => {
    try {
      const res = await axios.post("http://localhost:5000/api/contas/testar", {
        api_key: form.api_key,
        secret_key: form.secret_key,
        corretora: form.corretora
      });

      alert(
        res.data.status === "online"
          ? "✅ Conexão bem-sucedida!"
          : `❌ Erro: ${res.data.message}`
      );
    } catch (err) {
      alert(`❌ Falha na conexão: ${err.response?.data?.message || err.message}`);
    }
  };

  return (
    <div className="contas-container">
      <div className="contas-form-box">
        <form onSubmit={handleSubmit}>
          <h2>{editId ? "Editar Conta" : "Cadastro de Conta de Corretora"}</h2>
          <input
            name="nome"
            placeholder="Nome da conta"
            value={form.nome}
            onChange={handleChange}
            required
          />
          <select name="corretora" value={form.corretora} onChange={handleChange}>
            <option value="Binance">Binance</option>
            <option value="Gate.io">Gate.io</option>
          </select>
          <input
            name="api_key"
            placeholder="API Key"
            value={form.api_key}
            onChange={handleChange}
            required
          />
          <input
            name="secret_key"
            placeholder="Secret Key"
            value={form.secret_key}
            onChange={handleChange}
            required
          />
          <div className="botoes-acao">
            <button type="submit" className="btn salvar">
              {editId ? "Atualizar" : "Salvar"}
            </button>
            <button type="button" className="btn testar" onClick={testarConexao}>
              Testar Conexão
            </button>
          </div>
        </form>
      </div>

      <div className="grid">
        <h3>Contas cadastradas</h3>
        <table>
          <thead>
            <tr>
              <th>Nome</th>
              <th>Corretora</th>
              <th>API Key</th>
              <th>Status</th>
              <th>Ações</th>
            </tr>
          </thead>
          <tbody>
            {contas.map((c) => (
              <tr key={c.id}>
                <td>{c.nome}</td>
                <td>{c.corretora}</td>
                <td>****{c.api_key.slice(-4)}</td>
                <td>
                  <span
                    style={{
                      color: c.status === "online" ? "green" : "red",
                      fontWeight: "bold"
                    }}
                  >
                    ●
                  </span>
                </td>
                <td>
                  <button onClick={() => handleEdit(c)}>Editar</button>
                  <button onClick={() => handleDelete(c.id)}>Excluir</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default Contas;
