import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './OrdensAuto.css';

const OrdensAuto = () => {
  const [contas, setContas] = useState([]);
  const [moedasDisponiveis, setMoedasDisponiveis] = useState([]);
  const [ordens, setOrdens] = useState([]);
  const [editId, setEditId] = useState(null);
  const [form, setForm] = useState({
    conta_id: '',
    corretora: '',
    moeda: '',
    valor_atual: '',
    percentual_compra: '',
    percentual_venda: '',
    valor_ordem: '',
    valor_compra: '',
    valor_venda: '',
    ativo: true,
    status: 'pendente'
  });

  const carregarContas = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/contas");
      setContas(res.data);
    } catch (err) {
      console.error("Erro ao carregar contas:", err);
      alert("Não foi possível carregar suas contas.");
    }
  };

  const carregarOrdens = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/ordens_auto');
      setOrdens(response.data);
    } catch (error) {
      console.error('Erro ao carregar ordens:', error);
    }
  };

  useEffect(() => {
    carregarContas();
    carregarOrdens();
  }, []);

  useEffect(() => {
    const carregarMoedas = async () => {
      try {
        if (form.corretora === 'binance') {
          const res = await axios.get('http://localhost:5000/api/cotacao/symbols');
          setMoedasDisponiveis(res.data);
        } else {
          setMoedasDisponiveis([]);
        }
      } catch (err) {
        setMoedasDisponiveis([]);
        console.error('Erro ao carregar moedas da corretora:', err);
      }
    };

    if (form.corretora) {
      carregarMoedas();
    }
  }, [form.corretora]);

  useEffect(() => {
    const va = parseFloat(form.valor_atual);
    const pc = parseFloat(form.percentual_compra);
    const pv = parseFloat(form.percentual_venda);

    let novosValores = {};
    if (!isNaN(va) && !isNaN(pc)) {
      novosValores.valor_compra = (va * (1 - pc / 100)).toFixed(2);
    }
    if (!isNaN(va) && !isNaN(pv)) {
      novosValores.valor_venda = (va * (1 + pv / 100)).toFixed(2);
    }
    if(Object.keys(novosValores).length > 0) {
      setForm(prev => ({ ...prev, ...novosValores }));
    }
  }, [form.valor_atual, form.percentual_compra, form.percentual_venda]);

  useEffect(() => {
    const buscarCotacao = async () => {
      if (!form.moeda || !form.corretora) return;

      try {
        const res = await axios.get(`http://localhost:5000/api/cotacao/binance/${form.moeda}`);
        const valor = parseFloat(res.data.valor_atual);
        setForm(prev => ({
          ...prev,
          valor_atual: valor.toFixed(2),
        }));
      } catch (err) {
        alert(`Erro ao buscar valor da moeda: ${err.message}`);
        setForm(prev => ({ ...prev, valor_atual: ''}));
        console.error(err);
      }
    };

    buscarCotacao();
  }, [form.moeda, form.corretora]);

  const resetForm = () => {
    setEditId(null);
    setForm({
      conta_id: '',
      corretora: '',
      moeda: '',
      valor_atual: '',
      percentual_compra: '',
      percentual_venda: '',
      valor_ordem: '',
      valor_compra: '',
      valor_venda: '',
      ativo: true,
      status: 'pendente'
    });
  };

  const handleEdit = (ordem) => {
    setEditId(ordem.id);
    const contaDaOrdem = contas.find(c => c.id === ordem.conta_id);
    setForm({
        ...ordem,
        valor_referencia: parseFloat(ordem.valor_referencia),
        corretora: contaDaOrdem ? contaDaOrdem.corretora.toLowerCase() : '',
    });
    window.scrollTo(0, 0);
  };

  const handleDelete = async (id) => {
    if (window.confirm("Tem certeza que deseja excluir esta ordem?")) {
        try {
            await axios.delete(`http://localhost:5000/api/ordens_auto/${id}`);
            alert('Ordem excluída com sucesso!');
            carregarOrdens();
        } catch (error) {
            alert('Erro ao excluir ordem.');
            console.error('Erro ao deletar:', error);
        }
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name === 'conta_id') {
      const contaSelecionada = contas.find(c => c.id === parseInt(value));
      setForm(prev => ({
        ...prev,
        conta_id: value,
        corretora: contaSelecionada ? contaSelecionada.corretora.toLowerCase() : '',
        moeda: '',
        valor_atual: ''
      }));
    } else {
      setForm(prev => ({ ...prev, [name]: value }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const url = editId 
        ? `http://localhost:5000/api/ordens_auto/${editId}` 
        : 'http://localhost:5000/api/ordens_auto';
    const method = editId ? 'put' : 'post';

    try {
      await axios[method](url, form);
      alert(`✅ Ordem ${editId ? 'atualizada' : 'salva'} com sucesso!`);
      resetForm();
      carregarOrdens();
    } catch (error) {
      alert(`Erro ao ${editId ? 'atualizar' : 'salvar'} ordem.`);
      console.error("Erro no submit:", error);
    }
  };

  const getStatusClassName = (status) => {
    switch (status?.toLowerCase()) {
      case 'comprado':
        return 'status-comprado';
      case 'vendido':
        return 'status-vendido';
      case 'pendente':
        return 'status-pendente';
      default:
        return '';
    }
  };

  return (
    <div className="ordens-container">
      <h2>{editId ? 'Editar Ordem Automática' : 'Nova Ordem Automática'}</h2>
      <form className="ordem-form" onSubmit={handleSubmit}>
        <div className="form-grid">
          <select name="conta_id" value={form.conta_id} onChange={handleChange} required>
            <option value="">Selecione a Conta</option>
            {contas.map(conta => (
              <option key={conta.id} value={conta.id}>
                {conta.nome} ({conta.corretora})
              </option>
            ))}
          </select>
          <select name="moeda" value={form.moeda} onChange={handleChange} required disabled={!form.corretora}>
            <option value="">Selecione a moeda</option>
            {moedasDisponiveis.map((moeda, idx) => (
              <option key={idx} value={moeda}>{moeda}</option>
            ))}
          </select>
          <input name="valor_atual" placeholder="Valor Atual" type="text" value={form.valor_atual} readOnly />
          <input name="valor_ordem" placeholder="Valor da Ordem (USDT)" type="number" step="0.01" value={form.valor_ordem} onChange={handleChange} required />
          <input name="percentual_compra" placeholder="% Queda p/ Compra" type="number" step="0.01" value={form.percentual_compra} onChange={handleChange} required />
          <input name="percentual_venda" placeholder="% Alta p/ Venda" type="number" step="0.01" value={form.percentual_venda} onChange={handleChange} required />
          <input name="valor_compra" placeholder="Valor de Compra" type="number" value={form.valor_compra} readOnly />
          <input name="valor_venda" placeholder="Valor de Venda" type="number" value={form.valor_venda} readOnly />
        </div>
        <button type="submit" className="btn-salvar">
          {editId ? 'Atualizar Ordem' : 'Salvar Ordem'}
        </button>
        {editId && (
            <button type="button" className="btn-cancelar" onClick={resetForm}>
                Cancelar Edição
            </button>
        )}
      </form>

      <h3>Ordens Cadastradas</h3>
      <table className="ordens-grid">
        <thead>
          <tr>
            <th>Conta</th>
            <th>Moeda</th>
            <th>Vlr. Referência</th>
            <th>% Compra</th>
            <th>% Venda</th>
            <th>Valor Ordem</th>
            <th>Valor Compra</th>
            <th>Valor Venda</th>
            <th>Status</th>
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {ordens.map((ordem) => (
            <tr key={ordem.id}>
              <td>{contas.find(c => c.id === ordem.conta_id)?.nome || 'N/A'}</td>
              <td>{ordem.moeda}</td>
              <td>${parseFloat(ordem.valor_referencia).toFixed(2)}</td>
              <td>{ordem.percentual_compra}%</td>
              <td>{ordem.percentual_venda}%</td>
              <td>${parseFloat(ordem.valor_ordem).toFixed(2)}</td>
              <td>${parseFloat(ordem.valor_compra).toFixed(2)}</td>
              <td>${parseFloat(ordem.valor_venda).toFixed(2)}</td>
              <td>
                <span className={getStatusClassName(ordem.status)}>
                  {ordem.status ? ordem.status.charAt(0).toUpperCase() + ordem.status.slice(1) : ''}
                </span>
              </td>
              <td className="botoes-acao">
                <button onClick={() => handleEdit(ordem)} className="btn-editar">Editar</button>
                <button onClick={() => handleDelete(ordem.id)} className="btn-excluir">Excluir</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default OrdensAuto;
