import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Configuracoes.css';

function Configuracoes() {
    const [form, setForm] = useState({
        telegram_bot_token: '',
        telegram_chat_id: ''
    });
    const [loading, setLoading] = useState(true);

    const API_URL = "http://localhost:5000/api";
    const token = localStorage.getItem('token');
    const authHeaders = { headers: { 'Authorization': `Bearer ${token}` } };

    useEffect(() => {
        const fetchConfig = async () => {
            if (!token) return;
            try {
                const { data } = await axios.get(`${API_URL}/configuracoes`, authHeaders);
                if (data) {
                    setForm(data);
                }
            } catch (error) {
                console.error("Erro ao buscar configurações:", error);
            } finally {
                setLoading(false);
            }
        };
        fetchConfig();
    }, [token]);

    const handleChange = (e) => {
        setForm({ ...form, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await axios.post(`${API_URL}/configuracoes`, form, authHeaders);
            alert("Configurações salvas com sucesso!");
        } catch (error) {
            console.error("Erro ao salvar configurações:", error);
            alert("Não foi possível salvar as configurações.");
        }
    };

    if (loading) return <div className="config-container">Carregando...</div>;

    return (
        <div className="config-container">
            <h2>Configurações do Sistema</h2>
            <form className="config-form" onSubmit={handleSubmit}>
                <h3>Notificações do Telegram</h3>
                <div className="form-group">
                    <label htmlFor="telegram_bot_token">Token do Bot</label>
                    <input 
                        type="text" 
                        id="telegram_bot_token"
                        name="telegram_bot_token" 
                        value={form.telegram_bot_token || ''}
                        onChange={handleChange}
                        placeholder="Cole o token do seu bot aqui"
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="telegram_chat_id">Chat ID</label>
                    <input 
                        type="text" 
                        id="telegram_chat_id"
                        name="telegram_chat_id" 
                        value={form.telegram_chat_id || ''}
                        onChange={handleChange}
                        placeholder="Seu ID de chat do Telegram"
                    />
                </div>
                <div className="form-actions">
                    <button type="submit" className="btn-salvar-config">Salvar Configurações</button>
                </div>
            </form>
        </div>
    );
}

export default Configuracoes;
