import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './GestaoFinanceira.css';

// --- Componente do Modal ---
const ContaModal = ({ conta, onClose, onSave }) => {
    const [formData, setFormData] = useState({
        nome: conta?.nome || '',
        tipo: conta?.tipo || 'conta_corrente',
        saldo: conta?.saldo || 0,
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({ ...prev, [name]: value }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        onSave(formData);
    };

    return (
        <div className="modal-backdrop">
            <div className="modal-content">
                <h2>{conta ? 'Editar Conta' : 'Adicionar Nova Conta'}</h2>
                <form onSubmit={handleSubmit}>
                    <div className="form-group">
                        <label>Nome da Conta</label>
                        <input type="text" name="nome" value={formData.nome} onChange={handleChange} placeholder="Ex: Banco Inter" required />
                    </div>
                    <div className="form-group">
                        <label>Tipo de Conta</label>
                        <select name="tipo" value={formData.tipo} onChange={handleChange}>
                            <option value="conta_corrente">Conta Corrente</option>
                            <option value="investimentos">Investimentos</option>
                            <option value="poupanca">Poupança</option>
                            <option value="caixa">Caixa (Dinheiro)</option>
                        </select>
                    </div>
                    <div className="form-group">
                        <label>Saldo (R$)</label>
                        <input type="number" name="saldo" step="0.01" value={formData.saldo} onChange={handleChange} required />
                    </div>
                    <div className="modal-actions">
                        <button type="button" onClick={onClose} className="btn-cancelar">Cancelar</button>
                        <button type="submit" className="btn-salvar">{conta ? 'Atualizar' : 'Salvar'}</button>
                    </div>
                </form>
            </div>
        </div>
    );
};


// --- Componente Principal ---
function GestaoFinanceira() {
    const [resumo, setResumo] = useState(null);
    const [loading, setLoading] = useState(true);
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [contaEmEdicao, setContaEmEdicao] = useState(null);

    const API_URL = "http://localhost:5000/api/gestao";
    const token = localStorage.getItem('token');
    const authHeaders = { headers: { 'Authorization': `Bearer ${token}` } };

    const carregarResumo = async () => {
        try {
            const { data } = await axios.get(`${API_URL}/resumo`, authHeaders);
            setResumo(data);
        } catch (err) {
            console.error("Erro ao carregar resumo financeiro:", err);
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        carregarResumo();
    }, []);

    const handleOpenModal = (conta = null) => {
        setContaEmEdicao(conta);
        setIsModalOpen(true);
    };

    const handleCloseModal = () => {
        setIsModalOpen(false);
        setContaEmEdicao(null);
    };

    const handleSaveConta = async (formData) => {
        try {
            if (contaEmEdicao) {
                // Editar
                await axios.put(`${API_URL}/contas/${contaEmEdicao.id}`, formData, authHeaders);
            } else {
                // Criar
                await axios.post(`${API_URL}/contas`, formData, authHeaders);
            }
            handleCloseModal();
            carregarResumo(); // Recarrega os dados para mostrar a nova conta
        } catch (error) {
            console.error("Erro ao salvar conta:", error);
            alert("Não foi possível salvar a conta.");
        }
    };

    const handleDeleteConta = async (id) => {
        if (window.confirm("Tem certeza que deseja excluir esta conta?")) {
            try {
                await axios.delete(`${API_URL}/contas/${id}`, authHeaders);
                carregarResumo();
            } catch (error) {
                console.error("Erro ao excluir conta:", error);
                alert("Não foi possível excluir a conta.");
            }
        }
    };

    const formatarValor = (valor, cor = false) => {
        const valorNumerico = parseFloat(valor || 0);
        const corClasse = !cor ? '' : valorNumerico >= 0 ? 'patrimonio-positivo' : 'patrimonio-negativo';
        return (
            <p className={corClasse}>
                R$ {valorNumerico.toLocaleString('pt-BR', { minimumFractionDigits: 2, maximumFractionDigits: 2 })}
            </p>
        );
    };

    if (loading) {
        return <div>Carregando sua gestão financeira...</div>;
    }

    return (
        <div className="gestao-container">
            {isModalOpen && <ContaModal conta={contaEmEdicao} onClose={handleCloseModal} onSave={handleSaveConta} />}
            <h2>Gestão Financeira</h2>

            <div className="resumo-grid-financeiro">
                <div className="resumo-card-financeiro"><h3>Patrimônio Cripto</h3>{formatarValor(resumo.saldoCripto)}</div>
                <div className="resumo-card-financeiro"><h3>Outros Ativos</h3>{formatarValor(resumo.saldoOutrosAtivos)}</div>
                <div className="resumo-card-financeiro"><h3>Passivos (Dívidas)</h3>{formatarValor(resumo.totalPassivos, true)}</div>
                <div className="resumo-card-financeiro"><h3>Patrimônio Líquido Total</h3>{formatarValor(resumo.patrimonioLiquido, true)}</div>
            </div>

            <section className="secao-financeira">
                <div className="secao-header">
                    <h3>Minhas Contas e Investimentos</h3>
                    <button className="btn-adicionar" onClick={() => handleOpenModal()}>Adicionar Conta</button>
                </div>
                <table>
                    <thead>
                        <tr>
                            <th>Nome</th>
                            <th>Tipo</th>
                            <th>Saldo</th>
                            <th>Ações</th>
                        </tr>
                    </thead>
                    <tbody>
                        {resumo?.contas?.map(conta => (
                            <tr key={conta.id}>
                                <td>{conta.nome}</td>
                                <td>{conta.tipo.replace('_', ' ')}</td>
                                <td>{formatarValor(conta.saldo)}</td>
                                <td className="botoes-acao-tabela">
                                    <button onClick={() => handleOpenModal(conta)} className="btn-editar">Editar</button>
                                    <button onClick={() => handleDeleteConta(conta.id)} className="btn-excluir">Excluir</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </section>
        </div>
    );
}

export default GestaoFinanceira;
