import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './OrdensAuto.css';

const OrdensAuto = () => {
  const [moedasDisponiveis, setMoedasDisponiveis] = useState([]);
  const [ordens, setOrdens] = useState([]);
  const [form, setForm] = useState({
    corretora: '',
    moeda: '',
    valor_atual: '',
    percentual_compra: '',
    percentual_venda: '',
    valor_ordem: '',
    valor_compra: '',
    valor_venda: ''
  });

  useEffect(() => {
    const carregarMoedas = async () => {
      try {
        if (form.corretora === 'binance') {
          const res = await axios.get('http://localhost:5000/api/cotacao/symbols');
          setMoedasDisponiveis(res.data);
        } else if (form.corretora === 'gateio') {
          const res = await axios.get(`/api/gateio/symbols`);
          setMoedasDisponiveis(res.data);
        } else {
          setMoedasDisponiveis([]);
        }
      } catch (err) {
        alert('Erro ao carregar moedas da corretora.');
        console.error(err);
      }
    };

    carregarMoedas();
  }, [form.corretora]);

  useEffect(() => {
    carregarOrdens();
  }, []);

  useEffect(() => {
    atualizarValores(form);
  }, [form.valor_atual, form.percentual_compra, form.percentual_venda]);

  useEffect(() => {
    if (form.moeda && form.corretora) {
      buscarCotacao();
    }
  }, [form.moeda, form.corretora]);

  const buscarCotacao = async () => {
    if (!form.moeda || !form.corretora) return;

    try {
      const res = await axios.get(`http://localhost:5000/api/cotacao/binance/${form.moeda}`);
      const valor = parseFloat(res.data.valor_atual);
      setForm(prev => ({
        ...prev,
        valor_atual: valor.toFixed(2),
        valor_compra: (valor * (1 - (parseFloat(prev.percentual_compra) || 0) / 100)).toFixed(2),
        valor_venda: (valor * (1 + (parseFloat(prev.percentual_venda) || 0) / 100)).toFixed(2)
      }));
    } catch (err) {
      alert(`Erro ao buscar valor da moeda: ${err.message}`);
      console.error(err);
    }
  };

  const carregarOrdens = async () => {
    try {
      const response = await axios.get('/api/ordens_auto');
      setOrdens(response.data);
    } catch (error) {
      alert('Erro ao carregar ordens automáticas.');
    }
  };

  const atualizarValores = (dados) => {
    const va = parseFloat(dados.valor_atual);
    const pc = parseFloat(dados.percentual_compra);
    const pv = parseFloat(dados.percentual_venda);

    if (!isNaN(va) && !isNaN(pc) && !isNaN(pv)) {
      setForm(prev => ({
        ...prev,
        valor_compra: (va - (va * pc) / 100).toFixed(2),
        valor_venda: (va + (va * pv) / 100).toFixed(2)
      }));
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;

    if (name === 'moeda') {
      // APENAS converte para maiúsculas, não remove outros caracteres
      const upper = value.toUpperCase(); 
      setForm(prev => ({ ...prev, [name]: upper }));
    } else {
      setForm(prev => ({ ...prev, [name]: value }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('/api/ordens_auto', form);
      alert('✅ Ordem salva com sucesso!');
      setForm({
        corretora: '',
        moeda: '',
        valor_atual: '',
        percentual_compra: '',
        percentual_venda: '',
        valor_ordem: '',
        valor_compra: '',
        valor_venda: ''
      });
      carregarOrdens();
    } catch (error) {
      alert('Erro ao salvar ordem.');
    }
  };

  return (
    <div className="ordens-container">
      <h2>Ordens Automáticas</h2>
      <form className="ordem-form" onSubmit={handleSubmit}>
        <div className="form-grid">
          <select name="corretora" value={form.corretora} onChange={handleChange} required>
            <option value="">Selecione a corretora</option>
            <option value="binance">Binance</option>
            <option value="gateio">Gate.io</option>
          </select>
          <select
            name="moeda"
            value={form.moeda}
            onChange={handleChange}
            onBlur={buscarCotacao}
            required
          >
            <option value="">Selecione a moeda</option>
            {moedasDisponiveis && moedasDisponiveis.length > 0 ? (
              moedasDisponiveis.map((moeda, idx) => (
                <option key={idx} value={moeda}>{moeda}</option>
              ))
            ) : (
              <option disabled>Sem moedas disponíveis</option>
            )}
          </select>
          <input name="valor_atual" placeholder="Valor Atual" type="text" value={form.valor_atual} readOnly />
          <input name="percentual_compra" placeholder="% Queda p/ Compra" type="number" value={form.percentual_compra} onChange={handleChange} required />
          <input name="percentual_venda" placeholder="% Alta p/ Venda" type="number" value={form.percentual_venda} onChange={handleChange} required />
          <input name="valor_ordem" placeholder="Valor da Ordem" type="number" value={form.valor_ordem} onChange={handleChange} required />
          <input name="valor_compra" placeholder="Valor de Compra" type="number" value={form.valor_compra} readOnly />
          <input name="valor_venda" placeholder="Valor de Venda" type="number" value={form.valor_venda} readOnly />
        </div>
        <button type="submit" className="btn-salvar">Salvar Ordem</button>
      </form>

      <h3>Ordens Cadastradas</h3>
      <table className="ordens-grid">
        <thead>
          <tr>
            <th>Corretora</th>
            <th>Moeda</th>
            <th>Valor Atual</th>
            <th>% Compra</th>
            <th>% Venda</th>
            <th>Valor Ordem</th>
            <th>Valor Compra</th>
            <th>Valor Venda</th>
          </tr>
        </thead>
        <tbody>
          {ordens.map((ordem, index) => (
            <tr key={index}>
              <td>{ordem.corretora}</td>
              <td>{ordem.moeda}</td>
              <td>{ordem.valor_atual}</td>
              <td>{ordem.percentual_compra}</td>
              <td>{ordem.percentual_venda}</td>
              <td>{ordem.valor_ordem}</td>
              <td>{ordem.valor_compra}</td>
              <td>{ordem.valor_venda}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default OrdensAuto;
