import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './OrdensAuto.css';

const OrdensAuto = () => {
  const [contas, setContas] = useState([]);
  const [moedasDisponiveis, setMoedasDisponiveis] = useState([]);
  const [ordens, setOrdens] = useState([]);
  const [editId, setEditId] = useState(null); // Estado para controlar a edição
  const [form, setForm] = useState({
    conta_id: '',
    corretora: '',
    moeda: '',
    valor_atual: '',
    percentual_compra: '',
    percentual_venda: '',
    valor_ordem: '',
    valor_compra: '',
    valor_venda: '',
    ativo: true,
    status: 'pendente'
  });

  // ... (todas as funções useEffect e carregarDados permanecem as mesmas)

  const resetForm = () => {
    setEditId(null);
    setForm({
      conta_id: '',
      corretora: '',
      moeda: '',
      valor_atual: '',
      percentual_compra: '',
      percentual_venda: '',
      valor_ordem: '',
      valor_compra: '',
      valor_venda: '',
      ativo: true,
      status: 'pendente'
    });
  };

  const handleEdit = (ordem) => {
    setEditId(ordem.id);
    const contaDaOrdem = contas.find(c => c.id === ordem.conta_id);
    setForm({
        ...ordem,
        valor_referencia: parseFloat(ordem.valor_referencia),
        corretora: contaDaOrdem ? contaDaOrdem.corretora.toLowerCase() : '',
    });
    window.scrollTo(0, 0); // Rola a tela para o topo para ver o formulário
  };

  const handleDelete = async (id) => {
    if (window.confirm("Tem certeza que deseja excluir esta ordem?")) {
        try {
            await axios.delete(`http://localhost:5000/api/ordens_auto/${id}`);
            alert('Ordem excluída com sucesso!');
            carregarOrdens();
        } catch (error) {
            alert('Erro ao excluir ordem.');
            console.error('Erro ao deletar:', error);
        }
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const url = editId 
        ? `http://localhost:5000/api/ordens_auto/${editId}` 
        : 'http://localhost:5000/api/ordens_auto';
    const method = editId ? 'put' : 'post';

    try {
      await axios[method](url, form);
      alert(`✅ Ordem ${editId ? 'atualizada' : 'salva'} com sucesso!`);
      resetForm();
      carregarOrdens();
    } catch (error) {
      alert(`Erro ao ${editId ? 'atualizar' : 'salvar'} ordem.`);
      console.error("Erro no submit:", error);
    }
  };

  // ... (a função getStatusClassName permanece a mesma)

  return (
    <div className="ordens-container">
      <h2>{editId ? 'Editar Ordem Automática' : 'Nova Ordem Automática'}</h2>
      <form className="ordem-form" onSubmit={handleSubmit}>
        {/* ... (o form-grid permanece o mesmo) ... */}
        <button type="submit" className="btn-salvar">
          {editId ? 'Atualizar Ordem' : 'Salvar Ordem'}
        </button>
        {editId && (
            <button type="button" className="btn-cancelar" onClick={resetForm}>
                Cancelar Edição
            </button>
        )}
      </form>

      <h3>Ordens Cadastradas</h3>
      <table className="ordens-grid">
        <thead>
          <tr>
            <th>Conta</th>
            <th>Moeda</th>
            <th>Valor Ordem</th>
            <th>Vlr. Compra</th>
            <th>Vlr. Venda</th>
            <th>Status</th>
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {ordens.map((ordem) => (
            <tr key={ordem.id}>
              <td>{contas.find(c => c.id === ordem.conta_id)?.nome || 'N/A'}</td>
              <td>{ordem.moeda}</td>
              <td>${parseFloat(ordem.valor_ordem).toFixed(2)}</td>
              <td>${parseFloat(ordem.valor_compra).toFixed(2)}</td>
              <td>${parseFloat(ordem.valor_venda).toFixed(2)}</td>
              <td>
                <span className={getStatusClassName(ordem.status)}>
                  {ordem.status.charAt(0).toUpperCase() + ordem.status.slice(1)}
                </span>
              </td>
              <td className="botoes-acao">
                <button onClick={() => handleEdit(ordem)} className="btn-editar">Editar</button>
                <button onClick={() => handleDelete(ordem.id)} className="btn-excluir">Excluir</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default OrdensAuto;
