import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './GestaoFinanceira.css';

const formatarValor = (valor) => `R$ ${parseFloat(valor || 0).toLocaleString('pt-BR', { minimumFractionDigits: 2, maximumFractionDigits: 2 })}`;

const ResumoCard = ({ titulo, valor, cor }) => (
    <div className="resumo-card-financeiro">
        <h3>{titulo}</h3>
        <p className={cor}>{formatarValor(valor)}</p>
    </div>
);

function GestaoFinanceira() {
    const [resumo, setResumo] = useState(null);
    const [loading, setLoading] = useState(true);

    const carregarResumo = async () => {
        try {
            const token = localStorage.getItem('token');
            const { data } = await axios.get("http://localhost:5000/api/gestao/resumo", {
                headers: { 'Authorization': `Bearer ${token}` }
            });
            setResumo(data);
        } catch (err) {
            console.error("Erro ao carregar resumo financeiro:", err);
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        carregarResumo();
    }, []);

    if (loading) {
        return <div>Carregando sua gestão financeira...</div>;
    }

    if (!resumo) {
        return <div>Não foi possível carregar os dados. Tente novamente mais tarde.</div>;
    }

    return (
        <div className="gestao-container">
            <h2>Gestão Financeira</h2>

            <div className="resumo-grid-financeiro">
                <ResumoCard titulo="Patrimônio Cripto" valor={resumo.saldoCripto} />
                <ResumoCard titulo="Outros Ativos" valor={resumo.saldoOutrosAtivos} />
                <ResumoCard titulo="Passivos (Dívidas)" valor={resumo.totalPassivos} cor="patrimonio-negativo" />
                <ResumoCard 
                    titulo="Patrimônio Líquido Total" 
                    valor={resumo.patrimonioLiquido} 
                    cor={resumo.patrimonioLiquido >= 0 ? 'patrimonio-positivo' : 'patrimonio-negativo'}
                />
            </div>

            <section className="secao-financeira">
                <div className="secao-header">
                    <h3>Minhas Contas e Investimentos</h3>
                    <button className="btn-adicionar">Adicionar Conta</button>
                </div>
                {/* Aqui virá a tabela de contas manuais */}
                <p>Em breve: adicione e gerencie suas contas bancárias, poupança e outros investimentos.</p>
            </section>

            <section className="secao-financeira">
                <div className="secao-header">
                    <h3>Despesas e Passivos</h3>
                    <button className="btn-adicionar">Adicionar Passivo</button>
                </div>
                 {/* Aqui virá a tabela de passivos */}
                <p>Em breve: controle suas faturas de cartão de crédito e financiamentos.</p>
            </section>

        </div>
    );
}

export default GestaoFinanceira;
