import React, { useEffect, useState } from "react";
import axios from "axios";
import {
  BarChart, Bar, XAxis, YAxis, Tooltip, ResponsiveContainer, LineChart, Line, CartesianGrid
} from "recharts";
import "./monitoramento.css";

function Monitoramento() {
  const [saldos, setSaldos] = useState([]);
  const [historico, setHistorico] = useState([]);
  const [resumo, setResumo] = useState({ saldo_inicial: 0, saldo_final: 0, percentual: "0.00" });

  const carregarDados = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/monitoramento");
      setSaldos(res.data.saldos);
      setHistorico(res.data.historico);
      setResumo(res.data.resumo);
    } catch (err) {
      console.error("Erro ao carregar dados de monitoramento:", err);
    }
  };

  useEffect(() => {
    carregarDados();
    const intervalo = setInterval(carregarDados, 30000);
    return () => clearInterval(intervalo);
  }, []);

  return (
    <div className="monitoramento-container">
      <h2>Monitoramento de Saldos</h2>

      <div className="resumo-box">
        <div><strong>Saldo Inicial:</strong> R$ {resumo.saldo_inicial.toFixed(2)}</div>
        <div><strong>Saldo Final:</strong> R$ {resumo.saldo_final.toFixed(2)}</div>
        <div><strong>Variação:</strong> {resumo.percentual}%</div>
      </div>

      <div className="grafico-box">
        <h3>Saldo por Corretora (Hoje)</h3>
        <ResponsiveContainer width="100%" height={300}>
          <BarChart data={saldos}>
            <XAxis dataKey="corretora" />
            <YAxis />
            <Tooltip />
            <Bar dataKey="saldo" fill="#8884d8" />
          </BarChart>
        </ResponsiveContainer>
      </div>

      <div className="grafico-box">
        <h3>Evolução do Saldo</h3>
        <ResponsiveContainer width="100%" height={300}>
          <LineChart data={historico}>
            <CartesianGrid stroke="#ccc" />
            <XAxis dataKey="data" />
            <YAxis />
            <Tooltip />
            <Line type="monotone" dataKey="saldo" stroke="#82ca9d" />
          </LineChart>
        </ResponsiveContainer>
      </div>
    </div>
  );
}

export default Monitoramento;
